package variables;

public class HomeWork {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		double a = 9.5;
		double b = 4.5;
		
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		
		
		double sum = a+b;
		double sub = a-b;
		double mult = a*b;
		double div = a/b;
		
		System.out.println(sum);   //14.0
		System.out.println(sub);    //5.0
		System.out.println(mult);   //42.75
		System.out.println(div);     //2.111111111111111
		
		
		System.out.println("The sum of 9.5 and 4.5 is equal to "+sum);
		System.out.println("The sub of 9.5 and 4.5 is equal to "+sub);
		System.out.println("The mult of 9.5 and 4.5 is equal to "+mult);
		System.out.println("The div of 9.5 and 4.5 is equal to "+div);



		
	
		
		 
	}
}
